krpano.action:
if(device.panovideosupport == false,
error('Sorry, but panoramic videos are not supported by your current browser!');
,
loadscene(videopano);
);

scene.action:
videointerface_addsource('1024x512', '%CURRENTXML%/media/laureates.sd.mp4|%CURRENTXML%/media/laureates.sd.webm|%CURRENTXML%/media/laureates.sound.mp3', '%CURRENTXML%/media/zm-i-1024_512.jpg');
videointerface_addsource('1920x960', '%CURRENTXML%/media/laureates.hd.mp4|%CURRENTXML%/media/laureates.hd.webm|%CURRENTXML%/media/laureates.sound.mp3', '%CURRENTXML%/media/zm-i-1920_960.jpg');
videointerface_addsource('3840x1920', '%CURRENTXML%/media/laureates.4k.mp4|%CURRENTXML%/media/laureates.4k.webm|%CURRENTXML%/media/laureates.sound.mp3', '%CURRENTXML%/media/zm-i-3840_1920.jpg');

if(device.ios,

videointerface_play('1024x512');
,
videointerface_play('1920x960');
);
